sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
		//this.rightNow = new GlideDateTime();

    },

	isDatePast: function(strDate){
		//Create GlideDateTime objects for the current date and the passed in date
		var rightNow = new GlideDateTime();
		var testDate = new GlideDateTime(strDate);

		//If the testDate is before rightNow then return true
		if(testDate.before(rightNow)){
			return true;
		} else {
			return false;
		}
	},

	isDateToday: function(strDate){
		var rightNow = new GlideDateTime();
		var targetDate = new GlideDateTime(strDate);
		var today = rightnow.getLocalDate();
		var isItToday = targetDate.getLocalDate();

		if(isItToday.compareTo(today) == 0){
			return true;			
		} else {
			return false;
		}
	},

    type: 'NeedItUtils'
};
